    a.style.backgroundColor = '#3182ce';
  };
  a.onmouseout = () => { // Restore background color on mouseout
    a.style.backgroundColor = '#4299e1';
  };
  li.appendChild(a);
  ul.appendChild(li);
});

// Append the unordered list to the links div
linksDiv.appendChild(ul);

// Append the links div to the nav element
nav.appendChild(linksDiv);

